import React, { Component } from "react";
import Web3 from "web3";
import detectEthereumProvider from "@metamask/detect-provider";
import { Nav } from "react-bootstrap";
import KryptoBird from "../abis/KryptoBird.json";

class App extends Component {
  async componentDidMount() {
    await this.loadWeb3();
    await this.loadBlockchainData();
  }

  async loadWeb3() {
    const provider = await detectEthereumProvider();

    if (provider) {
      console.log("ethereum wallet is connected");
      // window.Web3 = new Web3(provider);
      new Web3(window.ethereum);
    } else {
      console.log("no ethereum wallet detected");
    }
  }

  async loadBlockchainData() {
    const accounts = await window.Web3.eth.requestAccounts();
    this.setState({
      account: accounts[0],
    });

    const networkId = await window.ethereum.request({ method: "net_version" });
    const networkData = KryptoBird.networks[networkId];

    if (networkData) {
      const abi = KryptoBird.abi;
      console.log("abi" + abi);
      const address = networkData.address;
      const contract = new Web3().eth.Contract(abi, address);
      this.setState({ contract });
      const totalSupply = await contract.methods.totalSupply();
      this.setState({ totalSupply });
      console.log(this.state.totalSupply);

      for (let i = 1; i <= totalSupply; i++) {
        const KryptoBird = await contract.methods.kryptoBirdz(i - 1).call();
        this.setState({ kryptoBirdz: [...this.state.kryptoBirdz, KryptoBird] });
      }
      console.log(this.state.kryptoBirdz);
    } else {
      window.alert("Smart Contract not deployed");
    }
  }

  mint = (kryptoBird) => {
    console.log(kryptoBird);
    console.log(this.state.contract.methods.mint(kryptoBird));
    console.log(this.state.account);
    this.state.contract.methods
      .mint(kryptoBird)
      .send({ from: this.state.account })
      // .once("receipt", (receipt) => {
      //   this.setState({ kryptoBirdz: [...this.state.kryptoBirdz, KryptoBird] });
      // });
      .on("transactionHash", (hash) => {
        console.log("On transactionHash - ");
        console.log(hash);
      })
      .on("confirmation", (confirmationNumber, receipt) => {
        console.log("on confirmation - Confirmation Number");
        console.log(confirmationNumber);
        console.log("receipt");
        console.log(receipt);
      })
      .on("receipt", (receipt) => {
        console.log("on receipt");
        console.log(receipt);
      })
      .on("error", (error, receipt) => {
        console.log("on error");
        console.log("Transaction was rejected by the network with error");
        console.log(error);
        if (receipt) {
          console.log("A receipt was included with rejection as follows:");
          console.log(receipt);
        }
      });
  };

  constructor(props) {
    super(props);
    this.state = {
      account: "",
      contract: null,
      totalSupply: 0,
      kryptoBirdz: [],
    };
  }

  render() {
    return (
      <div>
        <Nav className="navbar navbar-dark fixed-top bg-dark flex-md-nowrap p-0 shadow">
          <div
            className="navbar-brand col-sm-3 col-md-3 mr-0"
            style={{ color: "white" }}
          >
            Krypto Birdz NFTs (Non Fungible Tokens)
          </div>
          <ul className="navbar-nav px-3">
            <li className="nav-item text-nowrap d-none d-sm-none d-sm-block">
              <small className="text-white">{this.state.account}</small>
            </li>
          </ul>
        </Nav>
        <div className="container-fluid mt-1">
          <div className="row">
            <main role="main" className="col-lg-12 d-flex text-center">
              <div
                className="content mr-auto ml-auto"
                style={{ opacity: "0.8" }}
              >
                <h1 style={{ color: "white" }}>
                  KryptoBirdz - NFT Marketplace
                </h1>
                <form
                  onSubmit={(event) => {
                    event.preventDefault();
                    const kryptoBird = this.kryptoBird.value;
                    this.mint(kryptoBird);
                  }}
                >
                  <input
                    type="text"
                    placeholder="Add a file location"
                    className="form-control mb-1"
                    ref={(input) => (this.kryptoBird = input)}
                  />
                  <input
                    type="submit"
                    className="btn btn-primary btn-black"
                    value="MINT"
                    style={{ margin: "6px" }}
                  />
                </form>
              </div>
            </main>
          </div>
        </div>
      </div>
    );
  }
}

export default App;
